import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './styles/login.css'
import { login } from '../accountManagement';


const Login = () => {
    const [isLoggingIn, setLoggingIn] = useState(false);
    const [employeeLogin, setEmployeeLogin] = useState({password: ""});


    const handleSubmit = () => setLoggingIn(true);

    useEffect(() => {
        if (isLoggingIn) {
            login();
            setLoggingIn(false);
        }
    }, [isLoggingIn]);


    return (
        <div className="login-container">
            <div className="left" />

            <Form className="login-form">
                <h2>Enter Login Information</h2>
                <Form.Group controlId="formBasicEmail">
                    <Form.Label>Username: </Form.Label>
                    <Form.Control placeholder="Enter username" />
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                    <Form.Label>Password: </Form.Label>
                    <Form.Control type="password" placeholder="Password" value={setEmployeeLogin.password}/>
                </Form.Group>

                <Button variant="secondary"
                    type="submit"
                    disabled={isLoggingIn}
                    onClick={handleSubmit}>
                    {isLoggingIn ? 'Logging in…' : 'Submit'}
                </Button>
            </Form>
        </div >
    );
}


export default Login;
