import { GET_EMPLOYEE } from "./graphql/queries"
import { useQuery } from '@apollo/react-hooks';


export const login = (name: any) => {
    const { loading, data } = useQuery(
        GET_EMPLOYEE,
        { variables: { name: name } }
    );
    console.log(data);
}
